$(function(){$("#form_validation").validate({rules:{checkbox:{required:!0},gender:{required:!0}},highlight:function(e){$(e).parents(".form-line").addClass("error")},unhighlight:function(e){$(e).parents(".form-line").removeClass("error")},errorPlacement:function(e,r){$(r).parents(".form-group").append(e)}}),$("#form_advanced_validation").validate({rules:{date:{customdate:!0},creditcard:{creditcard:!0}},highlight:function(e){$(e).parents(".form-line").addClass("error")},unhighlight:function(e){$(e).parents(".form-line").removeClass("error")},errorPlacement:function(e,r){$(r).parents(".form-group").append(e)}}),$.validator.addMethod("customdate",function(e,r){return e.match(/^\d\d\d\d?-\d\d?-\d\d$/)},"Please enter a date in the format YYYY-MM-DD."),$.validator.addMethod("creditcard",function(e,r){return e.match(/^\d\d\d\d?-\d\d\d\d?-\d\d\d\d?-\d\d\d\d$/)},"Please enter a credit card in the format XXXX-XXXX-XXXX-XXXX.")});
